          if (GetTheaterButton) {
               let IsPlayerInNormalMode = await WaitForElement(`#player .html5-video-player`)
               console.log(IsPlayerInNormalMode)
               if (IsPlayerInNormalMode) {
                    console.log(GetTheaterButton)
                    GetTheaterButton.click()
                    await sleep(1000)
                    IsPlayerInNormalMode = await WaitForElement(`#player .html5-video-player`)
                    if (IsPlayerInNormalMode) {
                         AutoTheaterMode()
                    }
               }
}

